function at_collapsible(){

    jQuery('div.bet-table > div > div.btn-collapse-holder > a').click(function(){
        if( jQuery(this).data('target') ){
            var selector = jQuery(this).data('target');
            collapse(selector, 'toggle');

            jQuery(this).children().toggleClass('expanded-false expanded-true');        
        }
    });

    var fnmap = {
      'toggle': 'toggle',
      'show': 'add',
      'hide': 'remove' };

    var collapse = function collapse(selector, cmd) {
      var targets = Array.from(document.querySelectorAll(selector));
      targets.forEach(function (target) {
        target.classList[fnmap[cmd]]('show');
      });
    };

}


function at_tooltipster() {
    if (jQuery().tooltipster) {

        var jQuerytooltipsterScreen = jQuery(".tooltipster-screen");
        var tooltipsterPopover = jQuery(".tooltipster-popover");
        var tooltipsterText = jQuery(".tooltipster-text");

        tooltipsterPopover.tooltipster({
            contentCloning: !0,
            side: ["right", "left", "top", "bottom"],
            interactive: "true",
            maxWidth: "320",
            theme: ["tooltipster-popover"],
            zIndex: 999,
            functionReady: function(instance, helper) {
                jQuery(".tooltipster-popover").on("click", ".close", function() {
                    instance.close()
                })
            }
        });

        tooltipsterText.tooltipster({
            minWidth: "100",
            delay: "0",
            theme: ["tooltipster-text"],
            side: ["right", "left"]
        }); 

        jQuerytooltipsterScreen.tooltipster({
            contentCloning: !0,
            maxWidth: 420,
            plugins: ["follower"],
            anchor: "right-center",
            delay: "0",
            theme: ["tooltipster-screen"],
            offset: [0, 0],
            repositionOnScroll: !0
        });

        jQuery(window).on("resize", function() {
            var windowWidth = 992;
            window.innerWidth >= windowWidth ? jQuerytooltipsterScreen.tooltipster("enable") : jQuerytooltipsterScreen.tooltipster("disable")
        })
    }
}


